import * as React from "react"

import { cn } from "@/lib/utils"

// Corner accent variants for Propcore style
type CornerAccent = "red" | "gold" | "black" | "none"

interface InputProps extends React.ComponentProps<"input"> {
  cornerAccent?: CornerAccent
}

const cornerAccentClasses = {
  red: "border-b-main",
  gold: "border-b-accent",
  black: "border-b-border",
  none: "",
}

function Input({ className, type, cornerAccent = "none", ...props }: InputProps) {
  if (cornerAccent === "none") {
    return (
      <input
        type={type}
        data-slot="input"
        className={cn(
          "flex h-11 w-full rounded-base border-3 border-border bg-secondary-background",
          "selection:bg-main selection:text-main-foreground",
          "px-4 py-2 text-sm font-base text-foreground",
          "file:border-0 file:bg-transparent file:text-sm file:font-heading file:uppercase file:tracking-wider",
          "placeholder:text-foreground/40",
          "focus-visible:outline-hidden focus-visible:bg-white focus-visible:border-main",
          "disabled:cursor-not-allowed disabled:opacity-50",
          "transition-colors duration-100",
          className,
        )}
        {...props}
      />
    )
  }

  return (
    <div className="relative">
      <input
        type={type}
        data-slot="input"
        className={cn(
          "flex h-11 w-full rounded-base border-3 border-border bg-secondary-background",
          "selection:bg-main selection:text-main-foreground",
          "px-4 py-2 text-sm font-base text-foreground",
          "file:border-0 file:bg-transparent file:text-sm file:font-heading file:uppercase file:tracking-wider",
          "placeholder:text-foreground/40",
          "focus-visible:outline-hidden focus-visible:bg-white focus-visible:border-main",
          "disabled:cursor-not-allowed disabled:opacity-50",
          "transition-colors duration-100",
          className,
        )}
        {...props}
      />
      <div
        className={cn(
          "absolute bottom-0 right-0 w-0 h-0",
          "border-b-[12px] border-l-[12px] border-l-transparent",
          cornerAccentClasses[cornerAccent],
        )}
      />
    </div>
  )
}

export { Input }
export type { InputProps, CornerAccent }
