"use client"

import * as SwitchPrimitive from "@radix-ui/react-switch"

import * as React from "react"

import { cn } from "@/lib/utils"

// Star icon for switch thumb
function SwitchStar({ className }: { className?: string }) {
  return (
    <svg viewBox="0 0 24 24" fill="currentColor" className={className}>
      <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z" />
    </svg>
  )
}

function Switch({
  className,
  ...props
}: React.ComponentProps<typeof SwitchPrimitive.Root>) {
  return (
    <SwitchPrimitive.Root
      data-slot="switch"
      className={cn(
        "peer relative inline-flex h-9 w-[68px] shrink-0 cursor-pointer items-center",
        "border-3 border-border",
        "rounded-base",
        "shadow-shadow-sm",
        "transition-colors duration-200",
        "focus-visible:outline-hidden focus-visible:ring-2 focus-visible:ring-main focus-visible:ring-offset-2 focus-visible:ring-offset-background",
        "disabled:cursor-not-allowed disabled:opacity-50 disabled:shadow-none",
        "data-[state=unchecked]:bg-secondary-background",
        "data-[state=checked]:bg-main",
        className,
      )}
      {...props}
    >
      <SwitchPrimitive.Thumb
        data-slot="switch-thumb"
        className={cn(
          "pointer-events-none relative flex items-center justify-center",
          "h-7 w-7 rounded-base",
          "border-3 border-border",
          "bg-secondary-background",
          "shadow-[2px_2px_0_var(--border)]",
          "transition-all duration-200 ease-in-out",
          "data-[state=unchecked]:translate-x-[2px]",
          "data-[state=checked]:translate-x-[31px]",
          // Show star when checked
          "[&>svg]:data-[state=unchecked]:opacity-0 [&>svg]:data-[state=unchecked]:scale-0",
          "[&>svg]:data-[state=checked]:opacity-100 [&>svg]:data-[state=checked]:scale-100",
        )}
      >
        <SwitchStar
          className={cn(
            "size-4 transition-all duration-200",
            "text-main",
          )}
        />
      </SwitchPrimitive.Thumb>
    </SwitchPrimitive.Root>
  )
}

export { Switch, SwitchStar }
